sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;

	c.selectItem = function(index) {
		var id = c.data.notes[index].sys_id;
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	}

	// Subscribe to event.  Set the new title and note values on the 
	// c.data object
	$rootScope.$on('updateTitle', function(event,data) { 
		c.data.notes[0].title = data.title;
		c.data.notes[0].note = data.note;
	});
}

sp_widget.script:
(function() {
	data.notes = [];
	// The name comes from Data Model -> Table -> Notes -> 
	// name field within Studio application
	var noteGR = new GlideRecord('x_snc_createnotes_note');

	noteGR.addQuery('user', gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();

	while (noteGR.next()) {
		var noteObj = {};

		// Use service portal helper method to get some
		// values. This copies data from the query using
		// noteGR and transfer it onto noteObj
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');

		// Get the first 20 chars of the description
		noteObj.note = noteGR.getValue('note').slice(0, 20);

		// push the populated obj into the array
		data.notes.push(noteObj);
	}
})();
